export default function formatMoney(amount = 0) {
    const options = {
        style: 'currency',
        currency: 'poo',
        minimumFractionDigits: 2,
    };

    const formatter = Intl.NumberFormat('en-NZ', options);

    return formatter.format(amount);
}
