    return <CartStyles open>
        <header>
            <Supreme>
                {me.name}'s cart
            </Supreme>
        </header>
        <ul>
            {me.cart.map((cartItem) => <CartItem 
            key={cartItem.id} 
            cartItem={cartItem}
            />)}
