import styled from "styled-components"
import { CSSTransition, TransitionGroup } from 'react-transition-group';

const Dot = styled.div`
    background: var(--red);
    color: white;
    border-radius: 50%;
    padding: 0.5rem;
    line-height: 2rem;
    min-width: 3rem;
    margin-left: 1rem;
    font-feature-settings: "tnum";
    font-variant-numeric: tabular-nums;
`;

const AnimationStyles = styled.span`
    position: relative;
    .count-enter {
        background: green
    }
    .count-enter-active {
        background: yellow;
    }
    .count-exit {
        background: blue;
    }
    .count-ex-t.active {

    }
`;

export default function CartCount({count}){
    return (
        <TransitionGroup>
            <CSSTransition 
                unmountOnExit 
                className="count" 
                classNames="count" 
                key={count}
                timeout={{enter: 400, exit: 400}}
            >
                <Dot>{count}</Dot>

            </CSSTransition>
        </TransitionGroup>
    )
}
