    query SINGLE_ITEM_QUERY {
        Product (where: {
            id: "60943c2b328461ef04ec7a1c"
        }) 
        {
            name
            price
            description
            photo {
                id
                image {
                    publicUrlTransformed
                }
            }
        }
    }
`;

export default function SingleProduct({ query }) {
    const {data, loading, error} = useQuery();
